    private void btnAddMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnAddMouseClicked
        // TODO add your handling code here:
        if (txtQuantity.getText().equals("")){
            JOptionPane.showMessageDialog(this, "Enter quantity value");
        }
        else{
            String productprice = String.valueOf(productPrice);

            String order[] = {product, txtQuantity.getText(), productprice};

            DefaultTableModel tbl = (DefaultTableModel) tblOrderList.getModel();
            tbl.addRow(order);
            txtQuantity.setText("");
        }
    }//GEN-LAST:event_btnAddMouseClicked

