    private void btnDeleteMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnDeleteMouseClicked
        // TODO add your handling code here:
        DefaultTableModel tbl = (DefaultTableModel) tblOrderList.getModel();
        int index = tblOrderList.getSelectedRow();
        int reply = JOptionPane.showConfirmDialog(this, "Are you sure do you want to delete this order", "Delete Order", JOptionPane.YES_NO_OPTION);
        if (reply == JOptionPane.YES_OPTION){
            tbl.removeRow(index);

            int numRow = tblOrderList.getRowCount();
            double tot = 0;

            for (int i = 0; i < numRow; i++){
                double val = Double.valueOf(tblOrderList.getValueAt(i, 2).toString());
                int quantity = Integer.valueOf(tblOrderList.getValueAt(i, 1).toString());
                tot += val* quantity;
            }
            txtTotal.setText(Double.toString(tot));
        }
    }//GEN-LAST:event_btnDeleteMouseClicked

