    private void btnConfirmMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnConfirmMouseClicked
        // TODO add your handling code here:
         int numRow = tblOrderList.getRowCount();
         int reply = JOptionPane.showConfirmDialog(this, "Do you want to proceed order?", "Order Confirmation", JOptionPane.YES_NO_OPTION);
         if (reply == JOptionPane.YES_OPTION){
            DefaultTableModel tbl = (DefaultTableModel) tblOrderList.getModel();
            String recieptHeader = "---------------------------------------------------------------------------------------------------------------------"
                                               +"\n                                                      SUMSANG TECH                                         "
                                               +"\n   Address: General Luna corner Muralla Streets, Intramuros, Manila, Philippines 1002     "
                                               +"\n                                         www.facebook.com/SUMSANGTECH                                 "
                                               +"\n                                            Contact Number:+639314159265                                \n"
                                               +"---------------------------------------------------------------------------------------------------------------------"
                                               +"\nItem Name                                                                          Price\n"
                                               +"---------------------------------------------------------------------------------------------------------------------\n";
            String recieptItem = "";
            double tot =0;
            for (int i = 0; i < numRow; i++){
                double price = Double.valueOf(tblOrderList.getValueAt(i, 2).toString());
                int quantity = Integer.valueOf(tblOrderList.getValueAt(i, 1).toString());
                String item =  String.valueOf(tblOrderList.getValueAt(i, 0));
                tot += price * quantity;
                recieptItem += item + "                                                                     " + tot +"\n" 
                            + quantity + " * " + price + "\n";
            }
            String recieptFooter = "---------------------------------------------------------------------------------------------------------------------"
                                  +"\nTotal Ammount:                                                       " + tot +"\n"
                                  +"*********************************************************************************************************************" +"\n"
                                  +"                                                   THANK YOU COME AGAIN                                              " +"\n"
                                  +"*********************************************************************************************************************" +"\n"
                                  +"                                            SOFTWARE BY:SUMSANG TECH                                                 ";
            
            JOptionPane.showMessageDialog(this,recieptHeader + "\n"+  recieptItem + "\n" + recieptFooter);
            tbl.setRowCount(0);
            txtTotal.setText("");
        }
       
    }//GEN-LAST:event_btnConfirmMouseClicked

